function ToggleVisibility(n,t){var i=document.getElementById(n),r=document.getElementById(t);r.style.display=i.checked?"":"none"}function AnimateVisibility(n,t){var r=document.getElementById(n),i=document.getElementById(t);r.checked?$(i).fadeIn(200):$(i).fadeOut(200)}function ClearLog(){$.ajax({type:"POST",url:"/Manage/ClearLog",data:{},success:function(){location.reload()}})}function AskDeletePaymentMethod(n){BootstrapDialog.show({title:"Confirm",message:"Are you sure you want to delete this payment method?",type:BootstrapDialog.TYPE_WARNING,buttons:[{label:"Delete",autospin:!0,action:function(t){document.getElementById("deleteForm_"+n).submit();t.close()}},{label:"Cancel",action:function(n){n.close()}}]})}function submitForm(n){document.getElementById(n).submit()}function getQueryVariable(n){for(var i,u,f=window.location.search.substring(1),r=f.split("&"),t=0;t<r.length;t++)if(i=r[t].split("="),i[0]==n)return u=i[1],decodeURIComponent(u);return null}